#include <iostream>#include <math.h>using namespace std;int main() {    double x, y, z, a, b ,c, rez;    cout << "\n\t x,y,z = " << endl;		    cin >> x >> y >> z;		    a = pow(tan(x+y),2);		    b = exp(y-z);	    c = sqrt(cos(x*x)+sin(z*z));    rez = a-b*c;    cout << "\n x = " << x << "\n y = " << y << "\n z = " << z     		<< "\nRezult = " << rez << endl;    return 0;}